<?php
// PHP script to generate a simple webpage with a heart image and a video

// Simulate a human heart function (as from the previous example)
class Heart {
    private $heartRate;

    public function __construct($heartRate = 72) {
        $this->heartRate = $heartRate;
    }

    public function beat() {
        return "The heart is beating at a rate of " . $this->heartRate . " beats per minute.";
    }

    public function pumpBlood() {
        return "The heart pumps blood through the body.";
    }
}

// Create a Heart object
$humanHeart = new Heart();

// Output HTML content
?>

<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Human Heart Simulation</title>
</head>
<body>
    <h1>Human Heart Simulation</h1>

    <p><?php echo $humanHeart->beat(); ?></p>
    <p><?php echo $humanHeart->pumpBlood(); ?></p>

    <h2>Heart Image</h2>
    <img src="data:image/jpeg;base64," alt="Human Heart Image" width="300">
    <!-- Ensure 'heart.jpg' is in the same directory as this PHP file or provide the correct path -->

